package com.example.homework2.data

import com.example.homework2.data.dto.Group
import com.example.homework2.data.dto.Item
import com.example.homework2.data.dto.NewsResponse
import com.example.homework2.data.dto.Profile
import com.example.homework2.domain.MainRepository
import com.example.homework2.domain.Post
import com.google.gson.Gson

class RepositoryImpl : MainRepository {

    override fun getAllPosts(): List<Post> {
        val response = Gson().fromJson(mockJson, NewsResponse::class.java).response
        return map(response.items, response.profiles, response.groups)
    }

    private fun map(items: List<Item>, profiles: List<Profile>, groups: List<Group>): List<Post> {
        val list = mutableListOf<Post>()
        for (item in items) {
            val displayName: String?
            val avatarUrl: String?
            if (item.sourceId < 0) {
                val currentItemGroup = groups.find { it.id == item.sourceId * -1 }
                displayName = currentItemGroup?.name
                avatarUrl = currentItemGroup?.photo100
            } else {
                val currentItemProfile = profiles.find { it.id == item.sourceId }
                displayName = currentItemProfile?.run { "$firstName $lastName" }
                avatarUrl = currentItemProfile?.photo100
            }
            val post = Post(
                avatarUrl = avatarUrl ?: "",
                displayName = displayName ?: "",
                date = item.date,
                sourceId = item.sourceId,
                photosUrl = item.attachments?.first()?.photo?.sizes?.map { it.url },
                id = item.postId,
                text = item.text,
                viewCount = item.views.count,
                commentsCount = item.comments.count,
                repostsCount = item.reposts.count,
                isFavorite = item.isFavorite,
                likesCount = item.likes.count
            )
            list.add(post)
        }
        return list
    }
}

